UPDATE `#__jtg_maps` SET param ='OpenLayers.Layer.XYZ("{name}", "https://a.tiles.wmflabs.org/hikebike/${z}/${x}/${y}.png",{displayOutsideMaxExtent: true, isBaseLayer: true, attribution: "Map Data © <a href=http://www.openstreetmap.org/copyright>OpenStreetMap</a> contributors", transitionEffect: "resize" })' WHERE param like '%OpenLayers.Layer.TMS("{name}", "http://toolserver.org/tiles/hikebike/", { type: "png", getURL: osm_getTileURL, displayOutsideMaxExtent: true, isBaseLayer: true, attribution: "Map Data © OpenStreetMap contributors", transitionEffect: "resize" })' ;
